CREATE proc [dbo].[usp_GetExcelData](
	@id int
)
as
begin
	if @id > 0
	begin
		select id, excelName, excel from ExcelUpload where id =@id
	end
	else
	begin
		select id, excelName, excel from ExcelUpload
	end
end
