/****** Object:  StoredProcedure [dbo].[usp_uploadExcel]    Script Date: 9/29/2020 3:44:19 PM ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO


CREATE proc [dbo].[usp_uploadExcel](
	@excelName varchar(50)=null,
	@excel varchar(max)=null,
	@status int = 0 out 
)
as
begin
	if exists (select 1 from ExcelUpload where excelName =@excelName)
	begin
		update ExcelUpload set excelName=(case when isnull(@excelName,'')='' then 'uploaded.xlsx' else @excelName end),
		excel=@excel,ModifyDate=getdate() where excelName =@excelName
		set @status = 1 
	end
	else
	begin
		insert into ExcelUpload(excel,excelName) values (@excel,@excelName)
		set @status=1
	end

	if @@error<>0
	begin
		set @status=0
	end
end
